draw(a--b--c--d--e--f--a);
dot(a); dot(b); dot(c); dot(d); dot(e); dot(f); dot(g); dot(h); dot(i);
draw(g--h--i--g);
draw(a--d, dashed);
draw(b--e, dashed);
draw(c--f, dashed);
draw((-g)--(-h)--(-i)--(-g), dashed);
