draw((-2.5,0)--(2.5,0));
draw((0,-2.5)--(0,2.5));

draw((1,-0.1)--(1,0.1));
draw((2,-0.1)--(2,0.1));
draw((-1,-0.1)--(-1,0.1));
draw((-2,-0.1)--(-2,0.1));
draw((-0.1,1)--(0.1,1));
draw((-0.1,2)--(0.1,2));
draw((-0.1,-1)--(0.1,-1));
draw((-0.1,-2)--(0.1,-2));

label("$y = f(x)$", (2.8,0.6), red);
label("$x = f(y)$", (2.8,-0.5), blue);
